import React from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import { IconButton } from '@material-ui/core';
import Typography from '@material-ui/core/Typography';
import { AddShoppingCart, FavoriteBorder } from '@material-ui/icons';

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
    margin: 10,
  },
  media: {
    height: 140,
  },
});

function MediaCard(props) {
  const classes = useStyles();

  return (
    <Card className={classes.root}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image="/images/img1.jpg"
          title={props.title}
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            {props.title}
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            {props.description}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <IconButton color="primary" onClick={props.onClickAddToCart}>
          <AddShoppingCart />
        </IconButton>
        <IconButton color="primary" onClick={props.onClickAddFavorite}>
          <FavoriteBorder />
        </IconButton>
      </CardActions>
    </Card>
  );
}

MediaCard.propTypes = {
  className: PropTypes.string,
  title: PropTypes.string,
  description: PropTypes.string,
  isCanSeeMore: PropTypes.bool,
  label: PropTypes.string,
  cost: PropTypes.string,
  onClickAddToCart: PropTypes.func,
  onClickAddFavorite: PropTypes.func,
};

MediaCard.defaultProps = {
  className: "",
  title: "Test",
  description: "Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging\
  across all continents except Antarctica",
  onClickAddToCart: () => {},
  onClickAddFavorite: () => {},
};

export default MediaCard;
