import React from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import { IconButton, Collapse, Typography, Chip } from '@material-ui/core';
import { AddShoppingCart, FavoriteBorder, ExpandMore } from '@material-ui/icons';

const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: 345,
    margin: 10,
  },
  media: {
    height: 200,
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  chip: {
    margin: theme.spacing(0.5),
  }
}));

function MediaCard(props) {
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  return (
    <Card className={classes.root}>
      <CardActionArea>
        <Chip className={classes.chip} color="primary" label={props.label} />
        <CardMedia
          className={classes.media}
          image={props.image}
          title={props.title}
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            {props.title}
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            {props.summarize}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <IconButton color="primary" onClick={props.onClickAddToCart}>
          <AddShoppingCart />
        </IconButton>
        <IconButton color="primary" onClick={props.onClickAddFavorite}>
          <FavoriteBorder />
        </IconButton>
        <IconButton
          className={clsx(classes.expand, {
            [classes.expandOpen]: expanded,
          })}
          onClick={handleExpandClick}
          aria-expanded={expanded}
          aria-label="show more"
        >
          <ExpandMore />
        </IconButton>
      </CardActions>
      <Collapse in={expanded} timeout="auto" unmountOnExit>
        {props.description}
      </Collapse>
    </Card>
  );
}

MediaCard.propTypes = {
  title: PropTypes.string,
  description: PropTypes.string,
  summarize: PropTypes.string,
  isCanSeeMore: PropTypes.bool,
  label: PropTypes.string,
  cost: PropTypes.string,
  onClickAddToCart: PropTypes.func,
  onClickAddFavorite: PropTypes.func,
  image: PropTypes.string,
};

MediaCard.defaultProps = {
  title: "Test",
  summarize: "",
  label: "hot",
  description: "Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging\
  across all continents except Antarctica",
  onClickAddToCart: () => {},
  onClickAddFavorite: () => {},
  image: 'images/img1.jpg',
};

export default MediaCard;
