import React from 'react';
import PropTypes from 'prop-types';
import { Grid } from '@material-ui/core';
import { Pagination } from '@material-ui/lab';
import { Cart } from 'components';

const Category = (props) => {
  const { page, pages, onChangePage, nCol, nRow, data } = props;
  const sizePerPage = nCol * nRow;
  const handleChangePage = (p) => {
    onChangePage(p);
  }

  return (
    <Grid container spacing={1}>
      {data.map((item, index) => (
        <Grid key={index} item xs={6} sm={12/nCol} >
          <Cart
            {...item}
          />
        </Grid>
      ))}
      <Pagination
        count={pages}
        page={page}
        onChange={handleChangePage}
      />
    </Grid>
  )
}

Category.propTypes = {
  data: PropTypes.instanceOf(Array),
  nCol: PropTypes.number,
  nRow: PropTypes.number,
  page: PropTypes.number,
  onChangePage: PropTypes.func,
  pages: PropTypes.number,
  isServerPaging: PropTypes.bool,
};

Category.defaultProps = {
  data: [],
  nCol: 4,
  nRow: 5,
  page: 0,
  pages: 13,
  onChangePage: () => {},
  isServerPaging: false,
};

export default Category;
