import React from 'react';
import PropTypes from 'prop-types';
import { Grid } from '@material-ui/core';
import { Pagination } from '@material-ui/lab';
import { Cart } from 'components';

const Category = (props) => {
  const { page, pages, onChangePage, nCol, nRow, data } = props;
  const sizePerPage = nCol * nRow;
  let _pages = pages;
  const [_page, setPage] = React.useState(page);
  const [_data, setData] = React.useState(data);
  React.useEffect(() => {
    if (!props.isServerPaging) {
      _pages = Math.ceil(data/sizePerPage);
    }
  }, []);
  React.useEffect(() => {
    if (!props.isServerPaging) {
      setData(data.slice((page - 1) * sizePerPage, page * sizePerPage));
    }
  }, [_page]);

  /**
   * Handle change page
   * @param {Object} e event
   */
  const handleChangePage = (e) => {
    console.log(e.target.value);
    if (!props.isServerPaging) {
      setPage(e.target.value);
    }
    onChangePage(e.target.value);
  }

  console.log('page', _page);
  return (
    <Grid container spacing={1}>
      {_data.map((item, index) => (
        <Grid key={index} item xs={6} sm={12/nCol} >
          <Cart
            {...item}
          />
        </Grid>
      ))}
      <Pagination
        count={_pages}
        page={_page}
        onChange={handleChangePage}
        showFirstButton
        showLastButton
        color="secondary"
      />
    </Grid>
  )
}

Category.propTypes = {
  data: PropTypes.instanceOf(Array),
  nCol: PropTypes.number,
  nRow: PropTypes.number,
  page: PropTypes.number,
  onChangePage: PropTypes.func,
  pages: PropTypes.number,
  isServerPaging: PropTypes.bool,
};

Category.defaultProps = {
  data: [],
  nCol: 4,
  nRow: 5,
  page: 1,
  pages: 13,
  onChangePage: () => {},
  isServerPaging: false,
};

export default Category;
