import React from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import { ListSubheader, ClickAwayListener } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
    backgroundColor: theme.palette.background.paper,
  },
  gridList: {
    width: 500,
    height: 450,
  },
  icon: {
    color: 'rgba(255, 255, 255, 0.54)',
  },
}));

function TitlebarGridList(props) {
  const { tileData } = props;
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <GridList cellHeight={180} className={classes.gridList}>
        <GridListTile key="Subheader" cols={2} style={{ height: 'auto' }}>
          <ListSubheader component="div">{props.label}</ListSubheader>
        </GridListTile>
        {tileData.map((tile) => (
          <ClickAwayListener onClickAway={() => props.onClick(tile.id)}>
            <GridListTile key={tile.img}>
              <img src={tile.img} alt={tile.title} />
              <GridListTileBar
                title={tile.title}
                subtitle={<span>{tile.cost}</span>}
              />
            </GridListTile>
          </ClickAwayListener>
        ))}
      </GridList>
    </div>
  );
}

TitlebarGridList.propTypes = {
  label: PropTypes.string,
  tileData: PropTypes.arrayOf({
    id: PropTypes.number,
    img: PropTypes.string,
    title: PropTypes.string,
    cost: PropTypes.number,
  }),
  onClick: PropTypes.func,
}

TitlebarGridList.defaultProps = {
  label: 'Hot Item December',
  tileData: [
    {
      id: 0,
      img: 'images/bg.jpg',
      title: 'title',
      cost: 10000000,
    },
    {
      id: 1,
      img: 'images/bg3.jpg',
      title: 'title',
      cost: 20000000,
    },
    {
      id: 2,
      img: 'images/bg2.jpg',
      title: 'title',
      cost: 17000000,
    },
  ],
  onClick: () => {},
}

export default TitlebarGridList;
